-- Project Euler Problem 8
-- https://projecteuler.net/problem=8

-- The number is so big, it has to be a string value
number = ''
numberArray = {}
highVal = 0

function getProduct(rangeStart)
	product = numberArray[rangeStart] -- The first value is the start value
	rangeEnd = rangeStart + 12 -- End of range is 12 chars later on the 13nd char
	rangeStart = rangeStart + 1 -- Because of line 11 we need to start with the second char in range (else we would use the first 2 times)
	for i=rangeStart, rangeEnd do
		product = product * numberArray[i] -- All other values are multiplied by it and each other
	end
	return product
end

-- Split the large number into chars in the table
for i=1, string.len(number) do
	table.insert(numberArray, string.sub(number, i, i))
end
-- For each range range 1 to range 987 (1000-13) check if it got a greater product
for i=1,987 do
	-- Compare for each range if the sum of the range is greater then the sum of the global high (if yes set it accordingly)
	if (getProduct(i) > highVal) then
		highVal = getProduct(i)
	end
end

print(highVal)
print("This programm took " .. os.clock() .. " seconds (" .. os.clock()/60 .. " minutes) for execution.")

-- Solution: 23514624000 (correct)
-- Runtime: 0.005 seconds